package Lec16;

public class Shift_Opt {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n = 48;
		System.out.println(n >> 3);// right shift
		int n1 = -48;
		System.out.println(n1 >> 3);// right shift
		System.out.println(-5 >> 1);// right shift
		System.out.println(5 >> 1);// right shift
		System.out.println(-67 >> 1);// right shift

		int a = 6;
		System.out.println(a << 1);// left shift
		int a1 = -16;
		System.out.println(a1  << 1);// left shift

	}

}
